import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Todo = () => {
    const [todos, setTodos] = useState([]);
    const [title, setTitle] = useState('');

    // Fetch todos from the backend
    const fetchTodos = async () => {
        const response = await axios.get('http://localhost:5000/api/todos');
        setTodos(response.data);
    };

    useEffect(() => {
        fetchTodos(); // Fetch todos on component mount
    }, []);

    // Add a new todo
    const addTodo = async () => {
        if (!title) return; // Prevent empty titles
        const response = await axios.post('http://localhost:5000/api/todos', { title });
        setTodos([...todos, response.data]); // Update state with new todo
        setTitle(''); // Clear input
    };

    // Delete a todo
    const deleteTodo = async (id) => {
        await axios.delete(`http://localhost:5000/api/todos/${id}`);
        setTodos(todos.filter(todo => todo._id !== id)); // Update state
    };

    return (
        <div>
            <h1>Todo App</h1>
            <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Add a new todo"
            />
            <button onClick={addTodo}>Add Todo</button>
            <ul>
                {todos.map(todo => (
                    <li key={todo._id}>
                        {todo.title}
                        <button onClick={() => deleteTodo(todo._id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Todo;
